INSERT INTO scenario_warmups (department, seniority, title, scenario_context, choices_json, mapped_role_dna_dimensions, nd_safe_notes) VALUES
-- Engineering Warmups
('Engineering', 'mid', 'The Urgent Bug', 'You discover a bug in production that affects 5% of users. Your sprint is fully committed and the bug is not causing data loss, just minor inconvenience. What do you do?', '[{"id":"A","label":"Fix it immediately, push other work back","why_it_matters":"Shows urgency bias toward user experience","signals":["high urgency orientation","user-first mindset"]},{"id":"B","label":"Add to next sprint backlog, monitor closely","why_it_matters":"Shows structured prioritization","signals":["process-oriented","risk-aware"]},{"id":"C","label":"Propose a quick mitigation today, full fix next sprint","why_it_matters":"Shows balanced pragmatism","signals":["creative problem-solving","balanced urgency"]}]', '["execution_style", "problem_solving_vectors"]', 'All choices are valid approaches. No choice indicates poor judgment.'),
('Engineering', 'senior', 'The Architecture Debate', 'Your team is split on whether to use microservices or a modular monolith for a new system. You have strong opinions but so does a respected peer. How do you proceed?', '[{"id":"A","label":"Present a detailed technical comparison and let data decide","why_it_matters":"Shows evidence-based decision making","signals":["analytical approach","objectivity-focused"]},{"id":"B","label":"Propose a time-boxed prototype of each approach","why_it_matters":"Shows experimentation mindset","signals":["hands-on validation","learning-oriented"]},{"id":"C","label":"Seek input from the broader team to build consensus","why_it_matters":"Shows collaborative decision-making","signals":["team-inclusive","consensus-builder"]}]', '["cognitive_patterns", "communication_style"]', 'Each approach has merits in different contexts.'),
('Engineering', 'lead', 'The Scope Creep', 'Mid-project, a PM wants to add a major feature claiming it is now critical. Your team is already stretched. What do you do?', '[{"id":"A","label":"Push back firmly—scope was agreed, this derails the timeline","why_it_matters":"Shows boundary enforcement","signals":["strong scope discipline","timeline focus"]},{"id":"B","label":"Negotiate a trade—new feature in, something else out","why_it_matters":"Shows pragmatic flexibility","signals":["negotiation skill","creative problem-solving"]},{"id":"C","label":"Take it to leadership for a priority call","why_it_matters":"Shows escalation judgment","signals":["appropriate escalation","decision deferral when needed"]}]', '["execution_style", "communication_style"]', 'All responses demonstrate valid leadership approaches.'),

-- Product Warmups
('Product', 'mid', 'The Feature Request', 'Your biggest customer wants a feature that would benefit them greatly but add complexity for all other users. What do you do?', '[{"id":"A","label":"Decline politely—product strategy over single customer needs","why_it_matters":"Shows principled prioritization","signals":["strategic thinking","willingness to say no"]},{"id":"B","label":"Explore if this could become a paid add-on or enterprise tier feature","why_it_matters":"Shows commercial creativity","signals":["business-minded","solution-oriented"]},{"id":"C","label":"Dig deeper into whether this reveals a broader unmet need","why_it_matters":"Shows curiosity and pattern-recognition","signals":["user research mindset","opportunity sensing"]}]', '["cognitive_patterns", "problem_solving_vectors"]', 'Each choice reflects a valid product philosophy.'),
('Product', 'senior', 'The Metrics Conflict', 'Your new feature improved engagement but hurt conversion. Leadership is divided on whether this is a win. How do you frame this?', '[{"id":"A","label":"Engagement is a leading indicator—conversion will follow, stay course","why_it_matters":"Shows long-term thinking","signals":["patience","leading indicator focus"]},{"id":"B","label":"This needs investigation—something is off, pause and analyze","why_it_matters":"Shows analytical rigor","signals":["data-driven caution","root cause focus"]},{"id":"C","label":"Propose an A/B test to optimize for both metrics simultaneously","why_it_matters":"Shows experimentation mindset","signals":["test-and-learn approach","balanced optimization"]}]', '["cognitive_patterns", "execution_style"]', 'All choices show thoughtful engagement with the problem.'),
('Product', 'lead', 'The Roadmap Conflict', 'Engineering leadership wants to allocate 40% of capacity to tech debt. Sales wants more features. You own the roadmap. What do you do?', '[{"id":"A","label":"Support engineering—tech debt compounds and limits future velocity","why_it_matters":"Shows long-term investment thinking","signals":["technical empathy","sustainability focus"]},{"id":"B","label":"Find a middle ground—25% tech debt, phased over quarters","why_it_matters":"Shows compromise and diplomacy","signals":["negotiation skill","stakeholder balancing"]},{"id":"C","label":"Tie tech debt to specific revenue-enabling outcomes to align both","why_it_matters":"Shows strategic framing","signals":["outcome-oriented","alignment creation"]}]', '["communication_style", "cognitive_patterns"]', 'Each approach has merit depending on organizational context.'),

-- Design Warmups
('Design', 'mid', 'The Usability vs Aesthetics', 'User testing shows your beautiful new design confuses users. Stakeholders love the look and launch is next week. What do you do?', '[{"id":"A","label":"Recommend a delay to fix the usability issues","why_it_matters":"Shows user advocacy","signals":["usability-first","quality over speed"]},{"id":"B","label":"Propose quick usability patches that preserve the aesthetic","why_it_matters":"Shows creative problem-solving","signals":["pragmatic design","time-constraint awareness"]},{"id":"C","label":"Launch with the issues, plan a fast-follow iteration based on live data","why_it_matters":"Shows iterative mindset","signals":["ship-and-learn","data-driven iteration"]}]', '["problem_solving_vectors", "execution_style"]', 'All approaches can be valid in different contexts.'),
('Design', 'senior', 'The Stakeholder Override', 'The VP of Marketing insists on a design direction you believe is wrong for users. You have data but they have authority. What do you do?', '[{"id":"A","label":"Present your data clearly but ultimately defer to their decision","why_it_matters":"Shows professional deference while advocating","signals":["advocacy with respect","organizational awareness"]},{"id":"B","label":"Propose a test—let the data decide between both approaches","why_it_matters":"Shows evidence-based compromise","signals":["experimentation mindset","diplomatic resolution"]},{"id":"C","label":"Escalate to the CPO with a clear recommendation","why_it_matters":"Shows escalation judgment","signals":["appropriate escalation","conviction in beliefs"]}]', '["communication_style", "culture_alignment"]', 'Each response shows professional maturity.'),
('Design', 'lead', 'The Resource Constraint', 'You have 3 designers and requests for 5 high-priority projects. All stakeholders claim theirs is most important. What do you do?', '[{"id":"A","label":"Force-rank with leadership to get a single priority list","why_it_matters":"Shows escalation and clarity-seeking","signals":["leadership alignment","priority clarity"]},{"id":"B","label":"Propose phased delivery—everyone gets something, just not everything at once","why_it_matters":"Shows creative resource management","signals":["stakeholder management","phased planning"]},{"id":"C","label":"Identify which projects can use templates or lighter design touch","why_it_matters":"Shows pragmatic design scaling","signals":["efficiency thinking","design system leverage"]}]', '["execution_style", "communication_style"]', 'All approaches demonstrate leadership thinking.'),

-- Sales Warmups
('Sales', 'mid', 'The Discount Pressure', 'A prospect loves your product but says they will only sign if you give a 40% discount. This would set a bad precedent. What do you do?', '[{"id":"A","label":"Hold firm—explain the value justifies the price, walk away if needed","why_it_matters":"Shows value confidence","signals":["price discipline","value selling"]},{"id":"B","label":"Offer alternatives—longer commitment, case study rights, or reduced scope","why_it_matters":"Shows creative negotiation","signals":["deal creativity","win-win seeking"]},{"id":"C","label":"Escalate to leadership to make a strategic exception decision","why_it_matters":"Shows appropriate escalation","signals":["strategic awareness","leadership involvement when needed"]}]', '["execution_style", "communication_style"]', 'All approaches are professionally valid.'),
('Sales', 'senior', 'The Competitor Move', 'Your prospect just received a compelling offer from your main competitor at a lower price. The deal was nearly closed. What do you do?', '[{"id":"A","label":"Focus on differentiation—articulate why you are worth the premium","why_it_matters":"Shows value confidence","signals":["competitive positioning","value articulation"]},{"id":"B","label":"Bring in executive support for a high-touch close","why_it_matters":"Shows resource leveraging","signals":["executive sponsorship","relationship escalation"]},{"id":"C","label":"Accept you may lose this one—focus energy on better-fit opportunities","why_it_matters":"Shows deal qualification judgment","signals":["pipeline management","opportunity cost awareness"]}]', '["problem_solving_vectors", "cognitive_patterns"]', 'Each approach shows sales maturity.'),
('Sales', 'lead', 'The Underperforming Rep', 'One of your reps has missed quota for 3 months. They are a great culture fit and work hard, but results are not there. What do you do?', '[{"id":"A","label":"Intensive coaching and a clear 30-day improvement plan","why_it_matters":"Shows investment in development","signals":["coaching commitment","structured improvement"]},{"id":"B","label":"Explore if they are in the wrong role—maybe SDR or AM would be better fit","why_it_matters":"Shows role-fit thinking","signals":["talent deployment","creative solutions"]},{"id":"C","label":"Have an honest conversation about whether this is the right fit","why_it_matters":"Shows directness with care","signals":["honest feedback","performance management"]}]', '["communication_style", "culture_alignment"]', 'All choices show thoughtful people management.'),

